package projecteuler;

import java.math.BigInteger;

public class Problem8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		BigInteger b = new BigInteger("0");
		String s = "";
		
		for (int i = 0; i <= 987; i++) {
			BigInteger c = new BigInteger(s.substring(i, i + 1))
			.multiply(new BigInteger(s.substring(i + 1, i + 2)))			
			.multiply(new BigInteger(s.substring(i + 2, i + 3)))
			.multiply(new BigInteger(s.substring(i + 3, i + 4)))
			.multiply(new BigInteger(s.substring(i + 4, i + 5)))
			.multiply(new BigInteger(s.substring(i + 5, i + 6)))
			.multiply(new BigInteger(s.substring(i + 6, i + 7)))
			.multiply(new BigInteger(s.substring(i + 7, i + 8)))
			.multiply(new BigInteger(s.substring(i + 8, i + 9)))
			.multiply(new BigInteger(s.substring(i + 9, i + 10)))
			.multiply(new BigInteger(s.substring(i + 10, i + 11)))
			.multiply(new BigInteger(s.substring(i + 11, i + 12)))
			.multiply(new BigInteger(s.substring(i + 12, i + 13)));	
			
			b=c.max(b);
		}

		System.out.println(b);

	}
}
