package projecteuler;

public class Problem8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long b = 0l;
		long c = 0l;
		String s = "";
		// System.out.println(s.substring(0, 1));

		for (int i = 0; i <= 987; i++) {
			c = Integer.parseInt(s.substring(i, i + 1))
					* Integer.parseInt(s.substring(i + 1, i + 2))
					* Integer.parseInt(s.substring(i + 2, i + 3))
					* Integer.parseInt(s.substring(i + 3, i + 4))
					* Integer.parseInt(s.substring(i + 4, i + 5))
					* Integer.parseInt(s.substring(i + 5, i + 6))
					* Integer.parseInt(s.substring(i + 6, i + 7))
					* Integer.parseInt(s.substring(i + 7, i + 8))
					* Integer.parseInt(s.substring(i + 8, i + 9))
					* Integer.parseInt(s.substring(i + 9, i + 10))
					* Integer.parseInt(s.substring(i + 10, i + 11))
					* Integer.parseInt(s.substring(i + 11, i + 12))
					* Integer.parseInt(s.substring(i + 12, i + 13));

			if (c > b) {
				b = c;
			}
		}

		System.out.println(b);

	}
}
